export async function getPosts(): Promise<Post[]> {
  const modules = import.meta.glob<MdxModule>('../posts/*.mdx', { eager: true });

  const posts: Post[] =

Object.entries(modules).map(([path, module]) => {
   const slug = path.replace('../posts/', '').replace('.mdx', '');
   return {
     slug,
     path,
     frontmatter

: module.frontmatter,
      component: module.default,
    };
  });

  posts.sort((a, b) => new Date(b.frontmatter.date).getTime() - new Date(a.frontmatter

.date).getTime());

  return posts;
}

