            const rendererProcessPid = mainWindow.webContents.

getOSProcessId();

            const mainStats = await pidusage(mainProcessPid);
            const rendererStats = await pidusage(rendererProcessPid);

            // Send data to the renderer process
            mainWindow.webContents.

send('process-metrics', {
                main: {
                    pid: mainProcessPid,
                    cpu: mainStats.cpu,
                    memory: (mainStats.memory / 1024 / 10

24).toFixed(2), // MB
                },
                renderer: {
                    pid: rendererProcessPid,
                    cpu: rendererStats.cpu,
                    memory: (rendererStats.memory / 102

4 / 1024).toFixed(2), // MB
                },
            });
        } catch (err) {
            console.error('Error getting process stats:', err);
        }
    },

1000); // Update every 1 second
}

// Handle requests from the renderer to stop monitoring (optional)
ipcMain.on('stop-monitoring', () => {
    clearInterval(usageInterval);

pidusage.clear();
    console.log('Monitoring stopped.');
});

